template:
:doodle {
        @grid: 1x20 / 100vw 100vh;
      }

      @size: @r(1px, 2px) @r(50px, 150px);
      background: linear-gradient(to bottom,
        hsla(0, 0%, 100%, @r(.5, 1)),
        hsla(0, 0%, 100%, 0)
      );
      filter: drop-shadow(0 0 8px #fff);
      position: absolute;
      left: @r(100vw);
      top: @r(-10vh, 90vh);
      animation: fall @r(8s, 20s) linear infinite;
      animation-delay: -@r(20s);

      @keyframes fall {
        to { transform: translate(-100vw, 100vh) rotate(45deg); }
      }

:doodle {
        @grid: 8 / 90%;
        @shape: circle;
      }
      transition: .2s @r(.6s);
      border-radius: @pick(100% 0, 0 100%);
      transform: scale(@r(.25, 1.25));
      background: hsla(
        calc(240 - 6 * @x * @y),
        70%, 68%, @r(.8)
      );

:doodle {
        @grid: 14 / 100vmax;
      }
      @random {
        border-left: 1px solid #5d81bc;
      }
      @random {
        border-top: 1px solid #5d81bc;
      }
      @random(.25) {
        background: linear-gradient(
          @p(#fff, tan, #5d81bc), @lp
        )
        50% / @r(60%) @lr
        no-repeat;
      }
      @random {
        filter: drop-shadow(0 0 10px #fff);
      }

:doodle {
        @grid: 1 / 100vw 100vh;
        display: grid;
        place-items: center;
      }
      @content: @svg(
        viewBox: 0 0 16 16 p 1;
        stroke: #aeacfb;
        stroke-width: .1;
        stroke-linecap: round;
        line*16x16 {
          draw: @r(2s);
          x1, y1, x2, y2: @p(
            @nx(-1) @ny(-1) @nx @ny,
            @nx @ny(-1) @nx(-1) @ny,
            @nx @ny(-1) @nx @ny
          );
        }
      );

:doodle {
        @grid: 1 / 35% auto;
        position: absolute;
        top: 0;
        left: 0;
        background: radial-gradient(pink, yellow, red, red);
        mask: @svg-polygon(split: 400; scale: .7; r: cos(7t)^4 + sin(7t) +.3;);
      }

:doodle {
        @grid: @p(2, 3) / 12em;
      }
      background-image: @doodle(
        @grid: @r4 / 100%;
        background: @doodle(
          @grid: @r4 / 100%;
          background: @svg(
            viewBox: 0 0 1 1;
            path {
              d: M 0 0 L 1 0 L 1 1;
              fill: #60569e;
            }
          );
        );
      );

:doodle {
        @grid: 1x35;
        @size: 100vw 100vh;
        gap: 1px;
        display: flex;
        flex-direction: column;
        justify-content: center;
      }
      background: #60569e;
      width: @rand(5%, 100%);
      height: 1.5%;
